INSERT INTO `slide` (`id`, `Ten`, `Hinh`, `NoiDung`, `link`, `created_at`, `updated_at`) VALUES
(1, 'slide1', 'media-1.jpg', 'nội dung slide 1 ', NULL, '2017-04-02 05:48:13', NULL),
(2, 'slide2', 'media-2.jpg', 'nội dung slide 2', NULL, '2017-04-02 05:48:42', '2017-04-02 05:35:14'),
(3, 'slide3', 'media-3.jpg\r\n', '<p>kh&ocirc;ng c&oacute; g&igrave;&nbsp;</p>', NULL, '2017-04-14 07:45:08', '2017-04-14 07:45:08'),
(4, 'slide 4', 'media-4.jpg', '<p>kh&ocirc;ng l&agrave;m sao cả&nbsp;</p>', 'ssssssssssssss', '2017-04-14 07:45:22', '2017-04-14 07:45:22'),
(5, 'Cô gái vẽ đơn xin nhập học theo phong cách manga: \'Mình tin Việt Nam có một cơ hội phát triển rất lớn\'', 'media-5.jpg', '<p>Bằng những bức vẽ mang đậm dấu ấn cá nhân, cô nàng 9x tài năng đã được nhận vào học tại trường Đại học trực tuyến đầu tiên của Việt Nam.\r\n\r\n</p>', NULL, '2017-04-14 07:47:49', '2017-04-14 07:47:49'),
(6, '\r\nHãy để những hình ảnh vui vẻ này chữa lành một ngày tồi tệ của chúng ta', 'media-6.jpg', '<p>Sẽ có một triệu thứ khiến chúng ta vui vẻ hơn trong một ngày buồn bã của chính mình, chỉ cần chúng đơn giản thôi cũng đủ để che đi cái khó khăn lớn của hiện tại rồi.</p>', NULL, '2017-04-14 07:48:12', '2017-04-14 07:48:12'),
(7, 'Những cái \'Được\' chứng tỏ người độc thân là sướng nhất trên đời\r\n', 'media-7.jpg', '<p>Sau khi bắt được nghi phạm vụ sát hại bé Linh, đại sứ Nhật Bản đã đến tận nhà gia đình tại Việt Nam để chia buồn. Hình ảnh ông cúi đầu xin lỗi cha của bé Linh đã khiến nhiều người không khỏi nể phục</p>', NULL, '2017-04-14 07:48:38', '2017-04-14 07:48:38'),
(8, 'Đại sứ Nhật đích thân đến báo tin, cúi đầu xin lỗi cha bé Linh', 'media-8.jpg', '<p>Sau khi bắt được nghi phạm vụ sát hại bé Linh, đại sứ Nhật Bản đã đến tận nhà gia đình tại Việt Nam để chia buồn. Hình ảnh ông cúi đầu xin lỗi cha của bé Linh đã khiến nhiều người không khỏi nể phục</p>', NULL, '2017-04-14 07:49:02', '2017-04-14 07:49:02'),
(9, 'Bán đảo Triều Tiên trước nguy cơ xung đột vũ trang', 'QkiL_47160108-nature-pic.jpg', '<p><strong>T&igrave;nh h&igrave;nh b&aacute;n đảo Triều Ti&ecirc;n đang căng thẳng nhất kể từ khi nước n&agrave;y thử hạt nh&acirc;n v&agrave;o năm 2006. C&oacute; những động th&aacute;i tiềm ẩn khiến căng thẳng c&oacute; thể tiếp diễn, nhưng c&oacute; những l&yacute; do khiến Mỹ kh&oacute; tấn c&ocirc;ng qu&acirc;n sự Triều Ti&ecirc;n.​</strong></p>', NULL, '2017-04-14 14:00:57', '2017-04-14 14:00:57');

