INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(3, 'tỉnh Bắc Ninh đã được chặt hạ và chuyển giao xong cho ông Nguyễn Văn Hùy', 'tinh-bac-ninh-da-duoc-chat-ha-va-chuyen-giao-xong-cho-ong-nguyen-van-huy', 'cây sưa 200 tuổi ở đình làng Đông Cốc, xã Hà Mãn, huyện Thuận Thành, tỉnh Bắc Ninh đã được chặt hạ và chuyển giao xong cho ông Nguyễn Văn Hùy', '<p><span style=\"color:rgb(71, 71, 71); font-family:times new roman; font-size:19px\">Ng&agrave;y 25/3, c&acirc;y sưa 200 tuổi ở đ&igrave;nh l&agrave;ng Đ&ocirc;ng Cốc, x&atilde; H&agrave; M&atilde;n, huyện Thuận Th&agrave;nh, tỉnh Bắc Ninh đ&atilde; được chặt hạ v&agrave; chuyển giao xong cho &ocirc;ng Nguyễn Văn H&ugrave;y (Đồng Kỵ, huyện Từ Sơn, Bắc Ninh) với mức gi&aacute; l&agrave; 26 tỷ đồng.</span></p>', 'WieB_media-3.jpg', 1, 0, 7, '2017-03-31 06:00:24', '2017-05-07 06:06:46'),
(4, 'Quân đội Hàn Quốc cho biết Triều Tiên đã tiến hành phóng thử tên lửa vào sáng nay', 'quan-doi-han-quoc-cho-biet-trieu-tien-da-tien-hanh-phong-thu-ten-lua-vao-sang-nay', 'Quân đội Hàn Quốc cho biết Triều Tiên đã tiến hành phóng thử tên lửa vào sáng nay tại khu vực gần thành phố Sinpo thuộc bờ biển phía đông nước này nhưng vụ thử nghiệm được cho là đã thất bại.', '<p>cuộc đời vẫn đẹp sao , <em><strong>t&igrave;nh y&ecirc;u vẫn đẹp sao&nbsp;</strong></em></p>', 'EXNd_551123.jpg', 1, 0, 1, '2017-03-31 06:31:04', '2017-04-24 03:54:33'),
(5, 'Tên lửa Triều Tiên phát nổ ngay khi rời bệ phóng', 'ten-lua-trieu-tien-phat-no-ngay-khi-roi-be-phong', 'Quân đội Hàn Quốc cho biết Triều Tiên đã tiến hành phóng thử tên lửa vào sáng nay tại khu vực gần thành phố Sinpo thuộc bờ biển phía đông nước này nhưng vụ thử nghiệm được cho là đã thất bại.', '<p><strong>Ph&oacute; tổng thống Mỹ thăm H&agrave;n Quốc</strong></p>\r\n\r\n<p>Vụ thử t&ecirc;n lửa của Triều Ti&ecirc;n diễn ra chỉ v&agrave;i giờ trước khi Ph&oacute; tổng thống Mỹ Mike Pence c&oacute; chuyến thăm H&agrave;n Quốc v&agrave;o h&ocirc;m nay.</p>\r\n\r\n<p><strong>T&ecirc;n lửa ph&aacute;t nổ gần như ngay tức th&igrave;</strong></p>\r\n\r\n<p><em>Reuters&nbsp;</em>dẫn lời một ph&aacute;t ng&ocirc;n vi&ecirc;n qu&acirc;n đội Mỹ cho biết Triều Ti&ecirc;n đ&atilde; ph&oacute;ng một t&ecirc;n lửa gần Sinpo, tr&ecirc;n bờ biển ph&iacute;a đ&ocirc;ng nước n&agrave;y v&agrave;o s&aacute;ng sớm nay, v&agrave; t&ecirc;n lửa đ&atilde; ph&aacute;t nổ gần như ngay tức th&igrave;.</p>\r\n\r\n<p>Trung t&aacute; Hải qu&acirc;n Mỹ Dave Benham, ph&aacute;t ng&ocirc;n vi&ecirc;n Bộ Tư lệnh Th&aacute;i B&igrave;nh Dương, cho hay cơ quan n&agrave;y đ&atilde; ph&aacute;t hiện v&agrave; theo d&otilde;i vụ ph&oacute;ng t&ecirc;n lửa đạn đạo của Triều Ti&ecirc;n, diễn ra v&agrave;o l&uacute;c 5h51 s&aacute;ng 16/4 giờ địa phương. &Ocirc;ng Benham x&aacute;c nhận t&ecirc;n lửa đ&atilde; ph&aacute;t nổ gần như ngay sau đ&oacute; v&agrave; giới chức Mỹ đang t&igrave;m hiểu xem đ&acirc;y l&agrave; t&ecirc;n lửa loại n&agrave;o.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode IMSCurrentEditorEditObject\" style=\"margin-bottom: 13px; padding: 0px; border: 0px; outline: none 0px; max-width: 100%; background-color: rgb(238, 238, 238); font-family: tahoma; color: rgb(71, 71, 71); width: 640px; margin-top: 0px !important; margin-right: auto !important; margin-left: auto !important;\">\r\n<div style=\"margin: 0px auto; padding: 0px; border: 0px; outline: none 0px; max-width: 100%;\"><img alt=\"\r\nMột vụ phóng thử tên lửa Musudan của Triều Tiên (Ảnh: Reuters)\r\n\" id=\"img_33be16c0-223b-11e7-b866-db7f15cab6b8\" src=\"https://dantricdn.com/2017/ten-lua-1492300339496-1492302322551.jpg\" style=\"background-color:rgb(221, 221, 221); border:0px; display:inline; margin:0px auto; max-width:100%; outline:none 0px; padding:0px; vertical-align:bottom; width:640px\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px auto; padding: 5px; border: 0px; outline: none 0px; max-width: 100%; text-align: center;\">\r\n<p>Một vụ ph&oacute;ng thử t&ecirc;n lửa Musudan của Triều Ti&ecirc;n (Ảnh: Reuters)</p>\r\n</div>\r\n</div>\r\n\r\n<p><strong>Vụ ph&oacute;ng diễn ra một ng&agrave;y sau cuộc duyệt binh</strong></p>\r\n\r\n<p>&ldquo;Triều Ti&ecirc;n đ&atilde; cố gắng ph&oacute;ng một t&ecirc;n lửa chưa được x&aacute;c định từ khu vực gần th&agrave;nh phố Sinpo v&agrave;o s&aacute;ng nay nhưng vụ thử nghiệm n&agrave;y được cho l&agrave; đ&atilde; thất bại&rdquo;,&nbsp;<em>Reuters</em>&nbsp;dẫn th&ocirc;ng b&aacute;o của Văn ph&ograve;ng Hội đồng Tham mưu trưởng Li&ecirc;n qu&acirc;n H&agrave;n Quốc cho biết.</p>\r\n\r\n<p>Hiện vẫn chưa c&oacute; nhiều th&ocirc;ng tin chi tiết v&agrave; ph&iacute;a H&agrave;n Quốc cũng đang v&agrave;o cuộc để ph&acirc;n t&iacute;ch vụ ph&oacute;ng thử t&ecirc;n lửa n&agrave;y.</p>\r\n\r\n<p>Nếu th&ocirc;ng tin do qu&acirc;n đội H&agrave;n Quốc đưa ra l&agrave; ch&iacute;nh x&aacute;c, th&igrave; vụ ph&oacute;ng t&ecirc;n lửa tr&ecirc;n diễn ra chỉ một ng&agrave;y sau khi Triều Ti&ecirc;n tổ chức lễ duyệt binh rầm rộ kỷ niệm 105 năm ng&agrave;y sinh của cố l&atilde;nh tụ Kim Nhật Th&agrave;nh. Tại lễ duyệt binh n&agrave;y, B&igrave;nh Nhưỡng cũng ph&ocirc; diễn sức mạnh của nhiều loại t&ecirc;n lửa mới.</p>\r\n\r\n<p>Th&aacute;ng trước, Triều Ti&ecirc;n cũng từng ph&oacute;ng một t&ecirc;n lửa đạn đạo từ ch&iacute;nh khu vực bờ biển ph&iacute;a đ&ocirc;ng nước n&agrave;y trước th&ecirc;m cuộc gặp cấp cao giữa Tổng thống Mỹ Donald Trump v&agrave; Chủ tịch Trung Quốc Tập Cận B&igrave;nh. Trong cuộc gặp n&agrave;y, hai nh&agrave; l&atilde;nh đạo cũng b&agrave;n thảo về việc kiềm chế chương tr&igrave;nh hạt nh&acirc;n v&agrave; t&ecirc;n lửa của Triều Ti&ecirc;n.</p>\r\n\r\n<p>Trước khi lễ duyệt binh của Triều Ti&ecirc;n diễn ra, giới quan s&aacute;t nhận định B&igrave;nh Nhưỡng c&oacute; thể tiến h&agrave;nh một vụ thử t&ecirc;n lửa hoặc hạt nh&acirc;n mới nh&acirc;n c&aacute;c sự kiện trọng đại của đất nước trong th&aacute;ng 4. Hải qu&acirc;n Mỹ tuần trước cũng đ&atilde; điều nh&oacute;m t&aacute;c chiến t&agrave;u s&acirc;n bay USS Carl Vinson tới gần b&aacute;n đảo Triều Ti&ecirc;n để ph&aacute;t đi t&iacute;n hiệu cứng rắn tới B&igrave;nh Nhưỡng.</p>', '5Ucv_46937038-nature-pic.jpg', 1, 100, 22, '2017-04-14 17:56:30', '2017-04-23 09:59:47'),
(6, 'Đại sứ Nhật đích thân đến báo tin, cúi đầu xin lỗi cha bé Linh', 'dai-su-nhat-dich-than-den-bao-tin-cui-dau-xin-loi-cha-be-linh', 'Sau khi bắt được nghi phạm vụ sát hại bé Linh, đại sứ Nhật Bản đã đến tận nhà gia đình tại Việt Nam để chia buồn.', '<p>S&aacute;ng ng&agrave;y 14/4, cảnh s&aacute;t Nhật Bản đ&atilde; bắt giam nghi phạm&nbsp;<a href=\"http://kul.vn/tags/yasumasa-shibuya\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; background-color: transparent; color: rgb(51, 122, 183); text-decoration: none; transition: all 0.2s ease-in-out; font-family: inherit; font-size: inherit; display: initial; border-bottom: 0px dotted rgb(51, 51, 51);\">Yasumasa Shibuya</a>&nbsp;c&oacute; li&ecirc;n can đến vụ s&aacute;t hại b&eacute; Linh. Th&ocirc;ng tin n&agrave;y đ&atilde; được b&aacute;o ngay cho gia đ&igrave;nh của b&eacute; ở Hưng Y&ecirc;n.</p>\r\n\r\n<p>Chiều c&ugrave;ng ng&agrave;y, &ocirc;ng Kunio Umeda, đại sứ Nhật Bản đ&atilde; c&ugrave;ng l&atilde;nh đạo huyện Kho&aacute;i Ch&acirc;u, tỉnh Hưng Y&ecirc;n đến nh&agrave; &ocirc;ng L&ecirc; Anh H&agrave;o, bố của b&eacute; Linh, để thắp nhang v&agrave; gửi lời thăm hỏi đến gia đ&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"http://cdn.kul.vn/uploads/media/original/58f0/e9/dai-su-nhat-cuoi-dau-xin-loi-2.jpg\" style=\"border:0px; box-sizing:border-box; height:auto; max-width:100%; text-rendering:geometricPrecision; vertical-align:middle\" /></p>\r\n\r\n<p>&Ocirc;ng Kumeda đ&iacute;ch th&acirc;n đến b&aacute;o tin cho gia đ&igrave;nh b&eacute; Linh tại Hưng Y&ecirc;n.</p>\r\n\r\n<p>&Ocirc;ng Umeda đ&atilde; trực tiếp th&ocirc;ng b&aacute;o cho gia đ&igrave;nh về tiến triển vụ &aacute;n b&ecirc;n Nhật Bản v&agrave; mong mọi người y&ecirc;n l&ograve;ng, tin tưởng v&agrave;o bản lĩnh của cảnh s&aacute;t Nhật.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, điều khiến mọi người ấn tượng nhất ch&iacute;nh l&agrave; bức ảnh &ocirc;ng Umeda c&uacute;i đầu xin lỗi trước &ocirc;ng H&agrave;o, thể hiện một n&eacute;t văn h&oacute;a v&ocirc; c&ugrave;ng đặc trưng của người Nhật.</p>\r\n\r\n<p>&Ocirc;ng H&agrave;o cho biết, ng&agrave;i đại sứ đ&atilde; đ&iacute;ch th&acirc;n đến tận đ&acirc;y để thay mặt nh&acirc;n d&acirc;n Nhật Bản gửi lời xin lỗi gia đ&igrave;nh v&agrave; mong con g&aacute;i &ocirc;ng được si&ecirc;u tho&aacute;t.</p>\r\n\r\n<p><img alt=\"\" src=\"http://cdn.kul.vn/uploads/media/original/58f0/e9/dai-su-nhat-cuoi-dau-xin-loi-1.jpg\" style=\"border:0px; box-sizing:border-box; height:auto; max-width:100%; text-rendering:geometricPrecision; vertical-align:middle\" /></p>\r\n\r\n<p>Ng&agrave;i đại sứ Nhật kh&ocirc;ng ngần ngại c&uacute;i đầu tạ lỗi với &ocirc;ng H&agrave;o, cha của b&eacute; Linh. (Ảnh: Facebook)</p>\r\n\r\n<p><em>&quot;D&ugrave; rằng t&ocirc;i biết c&oacute; xin lỗi bao nhi&ecirc;u th&igrave; ch&aacute;u Linh kh&ocirc;ng thể quay về được nữa, nhưng h&ocirc;m nay bắt giữ được nghi phạm khiến bản th&acirc;n t&ocirc;i v&agrave; người d&acirc;n đất nước Nhật cảm thấy nhẹ l&ograve;ng&quot;</em>, &ocirc;ng Kunio Umeda chia sẻ.</p>\r\n\r\n<p>Được biết s&aacute;ng 14/4, sau khi hay tin bắt được hung thủ, &ocirc;ng H&agrave;o đ&atilde; kh&ocirc;ng giấu được nỗi căm phẫn v&agrave; tuy&ecirc;n bố sẽ kh&ocirc;ng tha thứ. Tuy nhi&ecirc;n, c&oacute; lẽ ch&iacute;nh nhờ lời xin lỗi ch&acirc;n th&agrave;nh của ng&agrave;i đại sứ Nhật Bản đ&atilde; khiến &ocirc;ng H&agrave;o ngu&ocirc;i ngoai, thanh thản hơn.</p>\r\n\r\n<p>Ở Nhật Bản, c&uacute;i đầu l&agrave; một biểu hiện của l&ograve;ng khi&ecirc;m tốn cũng như cả sự k&iacute;nh trọng d&agrave;nh cho đối phương. Lời xin lỗi của họ kh&ocirc;ng đơn thuần l&agrave; nhận lỗi sai đ&oacute; về m&igrave;nh, m&agrave; c&ograve;n thể hiện tr&aacute;ch nhiệm của bản th&acirc;n với sai lầm đ&oacute; cũng như lời hứa sẽ khắc phục.</p>\r\n\r\n<p>Với người Nhật, kh&ocirc;ng ai ngạc nhi&ecirc;n chuyện n&agrave;y khi m&agrave; cả Thủ tướng v&agrave; c&aacute;c Bộ trưởng vẫn lu&ocirc;n sẵn s&agrave;ng nhận lỗi v&agrave; c&uacute;i đầu xin lỗi v&igrave; những những sai lầm của họ. Thậm ch&iacute; c&oacute; thể chỉ l&agrave; một lời n&oacute;i hớ h&ecirc;nh trước d&acirc;n ch&uacute;ng.</p>\r\n\r\n<p>Quay trở lại vụ &aacute;n, sau buổi thẩm vấn đầu ti&ecirc;n, nghi phạm vẫn chưa khai ra th&ecirc;m bất kỳ t&igrave;nh tiết n&agrave;o quan trọng. &Ocirc;ng Umeda khẳng định cảnh s&aacute;t Nhật chắc chắn sẽ đem hung thủ phơi b&agrave;y trước &aacute;nh s&aacute;ng.</p>', 'g7aT_media-7.jpg', 1, 0, 1, '2017-04-14 19:23:59', '2017-04-24 05:45:59'),
(7, 'Hàng xóm nói về nghi phạm sát hại bé gái Việt: \'Ông ta từng là một người cha tuyệt vời\'', 'hang-xom-noi-ve-nghi-pham-sat-hai-be-gai-viet-ong-ta-tung-la-mot-nguoi-cha-tuyet-voi', 'Sau khi Yasumasa Shibuya, nghi phạm vụ sát hại bé gái người Việt bị bắt giữ. Hàng xóm xung quanh đã bị sốc và không thể tin chuyện vừa xảy ra trước mắt mình', '<p>S&aacute;ng ng&agrave;y 14/4, cảnh s&aacute;t tỉnh Chiba đ&atilde; đến nh&agrave; v&agrave; bắt giữ nghi phạm 46 tuổi, &ocirc;ng Yasumasa Shibuya, đồng thời từng giữ chức hội trưởng gi&aacute;m hộ học sinh tại trường tiểu học Mutsumi Daini.</p>\r\n\r\n<p>Mẫu ADN t&igrave;m thấy tr&ecirc;n thi thể của b&eacute; Linh ho&agrave;n to&agrave;n tr&ugrave;ng khớp với ADN của Shibuya. Trước sự việc n&agrave;y, h&agrave;ng x&oacute;m xung quanh đ&atilde; kh&ocirc;ng giấu được cảm gi&aacute;c bị sốc khi người đ&agrave;n &ocirc;ng m&agrave; họ lu&ocirc;n cho l&agrave; mẫu mực từ trước đến nay lại c&oacute; li&ecirc;n can đến vụ việc n&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"http://cdn.kul.vn/uploads/media/original/58f0/83/nghi-pham-giet-be-gai-tai-nhat-7.jpg\" style=\"border:0px; box-sizing:border-box; height:auto; max-width:100%; text-rendering:geometricPrecision; vertical-align:middle\" /></p>\r\n\r\n<p>Shibuya trong mắt h&agrave;ng x&oacute;m vẫn lu&ocirc;n l&agrave; một người đ&agrave;n &ocirc;ng mẫu mực. (Ảnh: Asahi Shimbun)</p>\r\n\r\n<p>Mọi người cho biết, khi c&ograve;n l&agrave; hội trưởng gi&aacute;m hộ học sinh v&agrave;o năm 2016, &ocirc;ng Shibuya l&agrave; người tận t&acirc;m trong c&ocirc;ng việc. &Ocirc;ng lu&ocirc;n c&oacute; mặt để hướng dẫn, tr&ocirc;ng nom c&aacute;c học sinh, đảm bảo an to&agrave;n cho bọn trẻ đến trường v&agrave; về nh&agrave;. Thậm ch&iacute;, sau ca trực, &ocirc;ng vẫn thường n&aacute;n lại một thời gian nữa rồi mới rời khỏi.</p>\r\n\r\n<p>Một người d&acirc;n 60 tuổi chia sẻ với Mainichi:&nbsp;<em>&quot;C&aacute;ch đ&acirc;y một tuần, t&ocirc;i đ&atilde; tr&ocirc;ng thấy &ocirc;ng ta cũng với 5-6 người c&oacute; vẻ l&agrave; phụ huynh của học sinh trường Mutsumi Daini đang đi tuần tại ga Mutsuni khi trời đ&atilde; kh&aacute; muộn. Tr&ocirc;ng kh&ocirc;ng c&oacute; một t&iacute; g&igrave; h&igrave;nh ảnh của kẻ s&aacute;t nh&acirc;n.</em></p>\r\n\r\n<p><em>T&ocirc;i c&ograve;n nhớ c&aacute;ch đ&acirc;y v&agrave;i năm, t&ocirc;i cũng từng thấy Shibuya nắm tay 2 học sinh đến trường. T&ocirc;i đ&atilde; nghĩ &ocirc;ng ta hẳn l&agrave; một người cha v&ocirc; c&ugrave;ng tuyệt vời. Thật sự rất sốc&quot;.</em></p>', 'QmAc_46937038-nature-pic.jpg', 1, 0, 5, '2017-04-14 18:21:57', '2017-04-16 09:42:33'),
(8, 'Những công trình “khủng” chiếm vỉa hè bị ông Đoàn Ngọc Hải tháo dỡ', 'nhung-cong-trinh-khung-chiem-via-he-bi-ong-doan-ngoc-hai-thao-do', 'Khung mái che bằng sắt dài 25 mét của tòa nhà SaiGon Center, bồn hoa dài hàng chục mét của khách sạn New World và Khách sạn Majestic, hàng loạt bức tường “khủng” trước toà nhà Bộ Công Thương, Công ty quản lý nhà TP và cao ốc…', '<p style=\"text-align:center\">Tiểu đảo chiếm vỉa h&egrave; đường Đinh Ti&ecirc;n Ho&agrave;ng nằm trước Trường C&aacute;n bộ Quản l&yacute; n&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển n&ocirc;ng th&ocirc;n 2 bị ph&aacute; bỏ.</p>\r\n\r\n<div class=\"before-after VCCSortableInPreviewMode\" id=\"VCBeforeAfter1492301084309\" style=\"margin: 0px auto; padding: 0px; border: 0px; font-size: 12px; outline: none 0px; max-width: 100%; font-family: arial; font-weight: bold; color: rgb(71, 71, 71); width: 0px; height: 0px; overflow: hidden; position: relative;\" title=\"Dãy bồn hoa rộng 4 mét, dài hơn 10 mét chiếm vỉa hè đường Đồng Khởi bị phá bỏ\">\r\n<div class=\"ui-draggable\" id=\"dragwrapper38239755\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none 0px; max-width: 100%; position: absolute; z-index: 20; width: 0px; height: 0px; left: 0px; opacity: 0.25;\">\r\n<div id=\"drag38239755\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none 0px; max-width: 100%; width: 2px; height: 0px; background: rgb(34, 34, 34); position: absolute; left: 3px;\"><img alt=\"handle\" id=\"handle38239755\" src=\"https://dantri4.vcmedia.vn/web_images/controbuttonl.png\" style=\"background-color:rgb(221, 221, 221); border:0px; cursor:e-resize; display:inline; font-size:0px; height:56px; left:-3px; margin:0px auto; max-width:100%; outline:none 0px; padding:0px; position:relative; top:-28px; vertical-align:bottom; width:8px; z-index:100\" /></div>\r\n</div>\r\n\r\n<div class=\"panel-before baw50\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none 0px; max-width: 100%; height: 0px; position: absolu